var ua = navigator.userAgent
	,os = (/iphone|ipad|ipod/gi).test(ua) ? "ios" : 
		(/android/gi).test(ua) ? "android" :
		(/mac/gi).test(ua) ? "macOS" : 
		(/windows/gi).test(ua) ? "Windows" : "other"
	,imgWidth = M('#wrapper').css('clientWidth')
	,screenX = M.screen().width/2 - 23
	,cuIdx = 0
	,indiCount = 0
	,arr32 = []

if (os == 'ios' || os == 'android') {
	//init();
} else {
	var  adTop = document.querySelector('#adTop')
		,adBottom = document.querySelector('#adBottom')
		,adMiddle = document.querySelector('#adMiddle')
	document.querySelector('body').removeChild(adTop)
	document.querySelector('body').removeChild(adBottom)
	document.querySelector('body').removeChild(adMiddle)
}

window.addEventListener("DOMContentLoaded", initPage, false);
function initPage(){
	init()
	M('#indicator').css('left', screenX+'px')
	
	setInterval(function(){
		if (indiCount == 11) {
			indiCount = 0
		} else {
			indiCount++
		}
		M('#indicator .ico').css('backgroundPositionX', -1 * 25 * indiCount+'px')
	}, 100)
	
	M('#btnNext').on('click', function(){
		if (cuIdx == data.length-1) {
			alert('마지막 사연입니다.\n카스에 추천 부탁드려요~^^')
			return;
		}
		cuIdx++;
		appendImg()
		M.scroll(0)
	})
	M('#btnPrev').on('click', function(){
		if (cuIdx == 0) {
			alert('첫번째 사연입니다.\n카스에 추천 부탁드려요~^^')
			return;
		}
		cuIdx--;
		appendImg()
		M.scroll(0)
	})
	btnStory.addEventListener('click', executeKakaoStoryLink, false);
	btnKakao.addEventListener('click', executeURLLink, false);
}

// 초기화
function init() {
	insert32(data, data.length)
	appendImg()
}

function appendImg() {
	if (M('#title').selector.length != 0) {
		M('#title').remove();
	}
	if (M('#story').selector.length != 0) {
		M('#story').remove();
	}

	M('#wrapper').append('p', {
		 'id':'title'
	})

	M('#wrapper').append('p', {
		 'id':'story'
	})

	//console.log(data[arr32[cuIdx]]['story'])
	M('#story')
		.html(data[arr32[cuIdx]]['story'])
		.css('width', imgWidth+'px');

	M('#title')
		.html(data[arr32[cuIdx]]['name'])
		.css('width', imgWidth+'px');
}


