function insert32(data, leng) {
	ranValue = Math.floor(Math.random() * data.length)
	if (arr32.length === 0) {
		arr32.push(ranValue);
		insert32(data, leng)
	} else {
		if (arr32.length >= leng) {
			return arr32;
		} else {
			for (var i=0; i<arr32.length; i++) {
				if (arr32[i] === ranValue) {
					insert32(data, leng);
					break;
				}
			}
			
			if (arr32.length <= leng-1) {
				arr32.push(ranValue);
				insert32(data, leng)
			}
		}
	}
	return arr32
	
}

